const verifyPaymentInfo = function verifyPaymentInfo(purchaseInfo){
  return new Promise(function(resolve, reject){
    setTimeout(function(){
      if (purchaseInfo.username && purchaseInfo.price){
        resolve(); 
        resolve();
        resolve();
        resolve();
      } else {
        reject("No purchase info");
      }
    },2000);
  });
}


module.exports = {
  verifyPaymentInfo : verifyPaymentInfo 
}

