
const verifyPaymentInfo = function verifyPaymentInfo (purchaseInfo, cb) {
  return new Promise(function(resolve,reject){
    if(purchaseInfo.username && purchaseInfo.price && purchaseInfo.email && purchaseInfo.item)  {
      setTimeout(function(){
        resolve();
      }, 2000);
    } else {
      reject(); 
    }
  });
}



module.exports = {
  verifyPaymentInfo : verifyPaymentInfo 
}

