const verifyPaymentInfo = function(purchaseInfo) {
  return new Promise(function(resolve,reject){
    setTimeout(function(){
        if (purchaseInfo.username && purchaseInfo.price)  {
          resolve(); 
          resolve();
          resolve(); 
          resolve();
          resolve(); 
          resolve();
        } else {
          reject(); 
        }
      },2000);
  })
}


module.exports = {
  verifyPaymentInfo : verifyPaymentInfo 
}

