const verifyPaymentInfo = function(purchaseInfo) {
  return new Promise(function(resolve,reject){
    setTimeout(function (){
      if (purchaseInfo.username && purchaseInfo.price){
        resolve(); 
        resolve();
        resolve();
        resolve();
      } else {
        reject (new Error("Oops"));
      }
    },2000) 
  });
}


module.exports = {
  verifyPaymentInfo : verifyPaymentInfo 
}

